    def cmb_kumpir_tipi_secildi(self):
        secilen_index = self.cmb_kumpir_tipi.currentIndex()
        if(secilen_index < 2):
            self.cmb_Malzeme1.setEnabled(False)
            self.cmb_Malzeme2.setEnabled(False)
            self.cmb_Cikolata.setEnabled(False)
            self.cmb_Susleme.setEnabled(False)
        else:
            self.cmb_Malzeme1.setEnabled(True)
            self.cmb_Malzeme2.setEnabled(True)
            self.cmb_Cikolata.setEnabled(True)
            self.cmb_Susleme.setEnabled(True)
            
    def Btn_Hesapla_clicked(self):
        str_sonuc = ""
        secilen_index = self.cmb_kumpir_tipi.currentIndex()
        malzeme1 = self.cmb_Malzeme1.currentText()
        malzeme2 = self.cmb_Malzeme2.currentText()
        cikolata = self.cmb_Cikolata.currentText()
        susleme = self.cmb_Susleme.currentText()
        if(secilen_index < 2):
           str_sonuc = self.cmb_kumpir_tipi.itemText(secilen_index)
           self.edt_sonuc.setText(str_sonuc)
        else:
           # En sonunda seçili metni yazdırabiliriz
           str_sonuc = self.cmb_kumpir_tipi.itemText(secilen_index)
           self.edt_sonuc.setText(f"{malzeme1} {malzeme2} {cikolata} {susleme} {str_sonuc}") 
           
           
